```typescript
// src/components/common/IconMap.tsx
import React from 'react';
import * as OutlineIcons from '@heroicons/react/24/outline';

export type IconType = 
  | 'complete' 
  | 'star' 
  | 'goal' 
  | 'mobile' 
  | 'reward' 
  | 'schedule'
  | 'streak'
  | 'warning'
  | 'family'
  | 'allowance'
  | 'eye'
  | 'user'
  | 'wave'
  | 'fire'
  | 'target'
  | 'heart'
  | 'phone'
  | 'lock'
  | 'chart'
  | 'refresh'
  | 'delete'
  | 'save'
  | 'calendar'
  | 'settings'
  | 'tasks'
  | 'home'
  | 'gift'
  | 'close'
  | 'parent'
  | 'child'
  | 'money'
  | 'add'
  | 'rocket'
  | 'info'
  | 'trophy'
  | 'rejected'
  | 'baby'
  | 'switch'
  | 'notification';
  // Plus 60+ more icon types

interface IconProps {
  type: IconType;
  size?: number;
  color?: string;
  className?: string;
  'aria-label'?: string;
}

export const IconMap: React.FC<IconProps> = ({ type, size = 20, color, className, 'aria-label': ariaLabel }) => {
  const iconProps = {
    width: size,
    height: size,
    color,
    className,
    'aria-label': ariaLabel
  };

  switch (type) {
    case 'complete':
      return <OutlineIcons.CheckIcon {...iconProps} />;
    case 'star':
      return <OutlineIcons.StarIcon {...iconProps} />;
    case 'goal':
    case 'target':
