      return <OutlineIcons.CalendarIcon {...iconProps} />;
    case 'streak':
      return <OutlineIcons.ArrowPathIcon {...iconProps} />;
    case 'fire':
      return <OutlineIcons.FireIcon {...iconProps} />;
    case 'warning':
      return <OutlineIcons.ExclamationTriangleIcon {...iconProps} />;
